DROP TABLE IF EXISTS `customer`;CREATE TABLE `customer` (`id` BIGINT UNSIGNED AUTO_INCREMENT COMMENT '顧客ID',`name` VARCHAR(255) NOT NULL COMMENT '顧客名',`var1` VARCHAR(255) NOT NULL COMMENT '追加データ1',`var2` VARCHAR(255) COMMENT '追加データ2',`var3` VARCHAR(255) COMMENT '追加データ3',`var4` VARCHAR(255) COMMENT '追加データ4',`var5` VARCHAR(255) COMMENT '追加データ5',`var6` VARCHAR(255) COMMENT '追加データ6',`var7` VARCHAR(255) COMMENT '追加データ7',`var8` VARCHAR(255) COMMENT '追加データ8',`start_date` DATETIME COMMENT '開始日',`end_date` DATETIME COMMENT '終了日',PRIMARY KEY (`id`)) ENGINE=InnoDB charset=utf8;DROP TABLE IF EXISTS `campain`;CREATE TABLE `campain` (`id` BIGINT UNSIGNED AUTO_INCREMENT COMMENT 'ID',`customer_id` BIGINT UNSIGNED NOT NULL COMMENT '顧客ID',`description` VARCHAR(255) NOT NULL COMMENT 'メモ',PRIMARY KEY (`id`)) ENGINE=InnoDB charset=utf8;DROP TABLE IF EXISTS `tag`;CREATE TABLE `tag` (`id` BIGINT UNSIGNED AUTO_INCREMENT COMMENT 'ID',`campaign_id` BIGINT UNSIGNED COMMENT 'キャンペーンID',`adnw` VARCHAR(255) COMMENT 'アドネ識別子',`tag` DATETIME COMMENT 'タグ',PRIMARY KEY (`id`)) ENGINE=InnoDB charset=utf8;INSERT INTO `campain`(`id`,`customer_id`,`description`) VALUES ('1','1','非会員'),('2','1','課金_10代_男性'),('3','1','課金_10代_女性'),('4','1','課金_20代_男性'),('5','1','課金_20代_女性'),('6','1','課金_30代_男性'),('7','1','課金_30代_女性'),('8','1','課金_40以上_男性'),('9','1','課金_40以上_女性'),('10','1','非課金_10代_男性'),('11','1','非課金_10代_女性'),('12','1','非課金_20代_男性'),('13','1','非課金_20代_女性'),('14','1','非課金_30代_男性'),('15','1','非課金_30代_女性'),('16','1','非課金_40以上_男性'),('17','1','非課金_40以上_女性');INSERT INTO `customer`(`id`,`name`,`var1`,`var2`,`var3`,`var4`,`var5`,`var6`,`var7`,`var8`,`start_date`,`end_date`) VALUES ('1','','','','','','','','','','1970-01-01 09:00:00','1970-01-01 09:00:00'),('2','','','','','','','','','','1970-01-01 09:00:00','1970-01-01 09:00:00'),('3','','','','','','','','','','1970-01-01 09:00:00','1970-01-01 09:00:00'),('4','','','','','','','','','','1970-01-01 09:00:00','1970-01-01 09:00:0